$(function() {
        $("#top-image").ready(function() {
            $('.bxslider').bxSlider({
                auto: true,
                speed: 2000,
                pause: 5000,
                slideWidth: 1040,
            });
        });
            $(".page-top").click(function() {
                $("html,body").animate({
                    "scrollTop": 0
                }, 1000, 'swing');
            });
        });
